mapper.select:
        SELECT * FROM univ_comm
#         ORDER BY CAST(univ_rank AS UNSIGNED) ASC
        limit #{start}, #{pageSize}

mapper.insert:
insert into univ_class values (#{univ_id},#{univ_cant}, #{univ_eng}, #{univ_min}, #{univ_max}, #{class_etc}, #{class_link});
insert into univ_dormitory values (#{univ_id}, #{campus_on}, #{campus_off});
insert into univ_dtl values (#{univ_id}, #{univ_master}, #{univ_limit}, #{univ_leave}, #{univ_gpa}, #{univ_toefl_ibt}, #{univ_toefl_itp}, #{univ_ielts}, #{univ_toeic}, #{etc}, #{program_link});
insert into univ_schedule values (#{univ_id}, #{sch_start}, #{sch_end}, #{sch_vacation}, #{sch_nomi_end}, #{sch_app_end});

mapper.update:
update univ_class set univ_cant = #{univ_cant}, univ_eng = #{univ_eng}, univ_min = #{univ_min}, univ_max = #{univ_max}, class_etc = #{class_etc}, class_link = #{class_link} where univ_id = #{univ_id};
update univ_comm set univ_rank = #{univ_rank} where univ_id = #{univ_id};
update univ_dormitory set campus_on = #{campus_on}, campus_off = #{campus_off} where univ_id = #{univ_id};
update univ_dtl set univ_master = #{univ_master}, univ_limit = #{univ_limit}, univ_leave = #{univ_leave}, univ_gpa = #{univ_gpa}, univ_toefl_ibt = #{univ_toefl_ibt}, univ_toefl_itp = #{univ_toefl_itp}, univ_ielts = #{univ_ielts}, univ_toeic = #{univ_toeic}, etc = #{etc}, program_link = #{program_link} where univ_id = #{univ_id};
update univ_schedule set sch_start = #{sch_start}, sch_end = #{sch_end}, sch_vacation = #{sch_vacation}, sch_nomi_end = #{sch_nomi_end}, sch_app_end = #{sch_app_end} where univ_id = #{univ_id};
